<?php

/**
 * TrdUserPayTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TrdUserPayTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TrdUserPayTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TrdUserPay');
    }

    /*查找除了本身之外手机号码是否存在*/
    public  static function isExistPhone($phone,$notHupuuId = null){
        $query = self::getInstance()
            ->createQuery()
            ->select('id')
            ->where('phone = ?', $phone);

        if($notHupuuId) $query = $query->andWhere('hupu_uid != ?',$notHupuuId);
        $query =  $query->fetchOne();

        if($query)
            return true;
        else
            return false;
    }

}