<?php

/**
 * kllCardwareDataTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class kllCardwareDataTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object kllCardwareDataTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('kllCardwareData');
    }


    public static function getCardWareData($field,$_value) {
        $info = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere($field.' = ?',$_value)
            ->fetchArray();
        if(empty($info)) return false;
        return $info;
    }


    /**
     * 删除数据
     */
    public static function del_cardware($field,$id) {
        $data = self::getInstance()
            ->createQuery()
            ->select('*')
            ->andWhere($field.' = ?',$id)
            ->execute();
        return  $data->delete();
    }



}