<?php

/**
 * trdJournalUserRecommendTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class trdJournalUserRecommendTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object trdJournalUserRecommendTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('trdJournalUserRecommend');
    }


    # 获取点赞记录
    public function getRecommend($uid,$id) {
        return self::getInstance()->createQuery()
            ->where('user_id =?',$uid)
            ->addWhere('product_id = ?',$id)
            ->fetchOne();
    }

}