<?php

/**
 * trdRedisPersistenceTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class trdRedisPersistenceTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object trdRedisPersistenceTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('trdRedisPersistence');
    }

    public static function setRes($key, $res){
        $redisPersistence = self::getInstance()->findOneBy('key', $key);
        if($res){
            if(!$redisPersistence){
                $redisPersistence = new trdRedisPersistence();
                $redisPersistence->setKey($key);
            }
            $redisPersistence->setRes($res);
            $redisPersistence->save();
        }

        $res  = !empty($redisPersistence['res']) ? $redisPersistence['res'] : false;
        return $res;
    }

    public  static function delRes($key){
        self::getInstance()->createQuery()->delete()->where('`key` = ?',$key)->execute();

        return true;
    }
}