<?php

/**
 * trdShaiwuUserRecommendTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class trdShaiwuUserRecommendTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object trdShaiwuUserRecommendTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('trdShaiwuUserRecommend');
    }

    # 获取点赞记录
    public function getRecommend($uid,$id)
    {
        return self::getInstance()->createQuery()
            ->where('user_id =?',$uid)
            ->addWhere('product_id = ?',$id)
            ->fetchOne();
    }
}